<?php if( have_rows('numbers_items') ):?>    <section class="numbers">        <div class="wrap wrap--wide">            <div class="numbers__inner">                <h3 class="section-title anim"><?php _e('Facts & Figures','yafrica')?></h3>                <ul class="numbers-list anim">                    <?php while ( have_rows('numbers_items') ) : the_row();?>                        <li>                            <span class="numbers-list__numeric"><?php the_sub_field('title')?></span>                            <div class="numbers-list__text"><?php the_sub_field('description')?></div>                        </li>                    <?php endwhile?>                </ul>                <?php $file = get_field('file')?>                <?php $fileLinkTitle = get_field('file_link_title')?>                <?php if ($file && $fileLinkTitle): ?>                    <a href="<?php echo $file['url'] ?>" target="_blank" class="brdr-link anim"><?php echo $fileLinkTitle ?></a>                <?php endif; ?>            </div>        </div>            <?php if(!empty(get_field('items_logo','option'))):?>                <img src="<?php the_field('items_logo','option') ?>" alt="<?php _e('Some Numbers','yafrica')?>" class="numbers__sign anim">            <?php endif;?>    </section><?php endif; ?>